import React, { useState, useEffect, useRef } from "react";
import { db, auth, storage } from "../firebase-config";
import {
  collection,
  addDoc,
  where,
  serverTimestamp,
  onSnapshot,
  query,
  orderBy,
} from "firebase/firestore";
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import "../styles/Chat.css";

export const Chat = ({ room, setIsInChat }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [image, setImage] = useState(null);
  const messagesRef = collection(db, "messages");
  const chatEndRef = useRef(null);
  const [user, setUser] = useState({
    name: auth.currentUser.displayName,
    photoURL: auth.currentUser.photoURL,
  });

  useEffect(() => {
    const queryMessages = query(
      messagesRef,
      where("room", "==", room),
      orderBy("createdAt")
    );
    const unsuscribe = onSnapshot(queryMessages, (snapshot) => {
      let messages = [];
      snapshot.forEach((doc) => {
        messages.push({ ...doc.data(), id: doc.id });
      });
      console.log(messages);
      setMessages(messages);
    });

    return () => unsuscribe();
  }, [room]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Check if the new message is empty and no image is selected
    if (newMessage.trim() === "" && !image) {
      alert("Please type a message or select an image before sending.");
      return;
    }

    // Upload image to Firebase Storage
    if (image) {
      const imageRef = ref(storage, `images/${Date.now()}_${image.name}`);
      await uploadBytes(imageRef, image);
      const imageUrl = await getDownloadURL(imageRef);

      await addDoc(messagesRef, {
        text: newMessage,
        imageUrl,
        createdAt: serverTimestamp(),
        user: auth.currentUser.displayName,
        room,
      });

      setImage(null); // Reset image state
    } else {
      // If no image, just send text message
      await addDoc(messagesRef, {
        text: newMessage,
        createdAt: serverTimestamp(),
        user: auth.currentUser.displayName,
        room,
      });
    }

    setNewMessage("");
  };

  const handleFileChange = (event) => {
    if (event.target.files[0]) {
      setImage(event.target.files[0]);
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSubmit(event);
    }
  };

  return (
    <div className="chat-app">
      <div className="profile-container" style={styles.profileContainer}>
        <div className="profile-header" style={styles.profileHeader}>
          <img
            src={user.photoURL}
            alt="User Profile"
            style={styles.profilePicture}
          />
          <span style={styles.profileName}>{user.name}</span>
        </div>
      </div>
      <div className="chat-header" style={styles.chatHeader}>
        <h1 style={styles.welcomeText}>Room Name: {room}</h1>
      </div>
      <div className="messages">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`message ${message.user === auth.currentUser.displayName ? "user-message" : "other-message"}`}
          >
            <span className="user-name">{message.user} :</span>
            <div className="message-text">{message.text}</div>
            {message.imageUrl && (
              <img
                src={message.imageUrl}
                alt="Sent"
                className="message-image"
              />
            )}
          </div>
        ))}
        <div ref={chatEndRef} />
      </div>
      <form onSubmit={handleSubmit} className="new-message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(event) => setNewMessage(event.target.value)}
          onKeyDown={handleKeyPress} // Add key press handler
          className="new-message-input"
          placeholder="Type your message here..."
        />
        <input
          type="file"
          accept="image/*"
          onChange={handleFileChange}
          className="image-input"
        />
        <button type="submit" className="send-button">
          Send
        </button>
      </form>
      <div className="back-to-room-container">
        <button onClick={() => setIsInChat(false)} className="back-to-room-button">
          Back to Room Input
        </button>
      </div>
    </div>
  );
};

const styles = {
  profileContainer: {
    position: 'absolute',
    left: '20px',
    top: '20px',
    display: 'flex',
    alignItems: 'center',
    backgroundColor: '#222',
    padding: '10px',
    borderRadius: '5px',
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.2)',
  },
  profileHeader: {
    display: 'flex',
    alignItems: 'center',
  },
  profilePicture: {
    width: '40px',
    height: '40px',
    borderRadius: '50%',
    marginRight: '10px',
  },
  profileName: {
    fontSize: '14px',
    color: '#aaa',
  },
  chatHeader: {
    display: 'flex',
    justifyContent: 'flex-end', // Align content to the right
    padding: '10px 0',
    marginTop: '-1px'
  },
  welcomeText: {
    fontFamily: 'Roboto Mono, monospace', // Apply the funky font here
    fontSize: '35px', // Adjust size as needed
    color: '#007bff', // Adjust color as needed
  },
};
